mapper.insert:
 	   INSERT
	     INTO TBL_MNOTE A
	   (
	     A.NO
	   , A.TITLE
	   , A.CONTENT
	   , A.CREATE_DATE
	   )
	   VALUES
	   (
	   	 SEQ_NO.NEXTVAL
	   , #{ title }
	   , #{ content }
	   , #{ date }
	   )

       INSERT
	     INTO TBL_RECORD A
	   (
	     A.NO
	   , A.AUTHOR
	   , A.NOTE_DATE
	   , A.STATE
	   )
	   VALUES
	   (
	     (SELECT MAX(B.NO)
	        FROM TBL_MNOTE B)
	   , #{author}
	   , #{noteDate}
	   , #{state}
	   )

mapper.update:
	   UPDATE
            TBL_MNOTE A
        SET A.TITLE = #{ title }
          , A.CONTENT = #{ content }
      WHERE A.NO = #{memoNo }

	   UPDATE
             TBL_RECORD A
         SET A.NOTE_DATE = #{noteDate}
           , A.STATE = #{state}
       WHERE A.NO = #{no}

mapper.delete:
 	     DELETE
	       FROM TBL_MNOTE A
	      WHERE A.NO = #{memoNo}

	   UPDATE
             TBL_RECORD A
         SET A.NOTE_DATE = #{noteDate}
           , A.STATE = #{state}
       WHERE A.NO = #{no}
